# print(uniswap_contract.functions.allPairsLength().call())
# pprint.pprint(uniswap_router_abi)
deadline = int(time.time()) + (60 * 10)

quantityToSwap = w3.toWei(0.0001, 'ether')
balance = w3.eth.getBalance(address)
max_slippage = 0.20

if quantityToSwap > balance:
    print("Insufficient funds !")
    exit(0)

price = uniswap_router_contract.functions.getAmountsOut(quantityToSwap, [WETH, USDC]).call()[-1]
amount_out_min = int((1 - max_slippage) * price)
print(price, amount_out_min)

def _get_tx_params(value):
    return {
        "from": address,
        "value": value,
        "gas": gas,
        "nonce": w3.eth.getTransactionCount(address)
    }

func = uniswap_router_contract.functions.swapExactETHForTokens(amountOutMin=amount_out_min, path=[WETH, USDC], to=address, deadline=deadline)
transaction = func.buildTransaction(_get_tx_params(quantityToSwap))
signed_txn = w3.eth.account.sign_transaction(transaction, private_key=private_key)
w3.eth.sendRawTransaction(signed_txn.rawTransaction)

